import React from 'react';

function DiscountBanner() {
  return (
    <div className="w-full bg-primary-600 py-4 px-4 flex items-center justify-center relative overflow-hidden">
      <div className="marquee-track flex items-center">
        <div className="marquee-content flex items-center gap-4">
        <span>ðŸŽ‰ Get 10% OFF when you choose the Premium Annual Plan! Only $89/month, billed annually. Save $132!</span>
        <a
          href="#pricing"
          className="ml-4 underline text-yellow-200 hover:text-yellow-300 font-bold"
        >
          Claim Your Discount
        </a>
      </div>
      {/* Duplicate for seamless effect */}
      <div className="marquee-content flex items-center gap-4" aria-hidden="true">
          <span>ðŸŽ‰ Get 10% OFF when you choose the Premium Annual Plan! Only $89/month, billed annually. Save $132!</span>
          <a
            href="#pricing"
            className="ml-4 underline text-yellow-200 hover:text-yellow-300 font-bold"
          >
            Claim Your Discount
          </a>
        </div>
      </div>
      <style>{`
      .marquee-track {
          width: 100%;
          display: flex;
          overflow: hidden;
          position: relative;
        }
        .marquee-content {
          white-space: nowrap;
          display: flex;
          animation: marquee 20s linear infinite;
        }
        @keyframes marquee {
          0% { transform: translateX(0); }
          100% { transform: translateX(-100%); }
        }
      `}</style>
    </div>
  );
}

export default DiscountBanner;
